from django.contrib.auth import get_user_model
from django.db import migrations

User = get_user_model()


def delete_profile_if_user_is_null(apps, schema_editor):
    """
    Function delete a profile if the profile as user as null and those not have an
    instance for the following models: Address, Receipt, Payment, and Invoice.
    If the profile has an instance, it will set the user to a super user
    for review.
    """
    CustomerProfileModel = apps.get_model('vendor', 'CustomerProfile')
    AddressModel = apps.get_model('vendor', 'Address')
    ReceiptModel = apps.get_model('vendor', 'Receipt')
    PaymentModel = apps.get_model('vendor', 'Payment')
    InvoiceModel = apps.get_model('vendor', 'Invoice')

    for profile in CustomerProfileModel.objects.filter(user__isnull=True):
        if not (AddressModel.objects.filter(profile=profile).exists() or
                ReceiptModel.objects.filter(profile=profile) or
                PaymentModel.objects.filter(profile=profile).exists() or
                InvoiceModel.objects.filter(profile=profile).exists()):
            profile.delete()
        else:
            # A superuser needs to review those profiles before deleting them.
            profile.user = User.objects.filter(superuser=True).first()
            profile.save()


class Migration(migrations.Migration):

    dependencies = [
        ('vendor', '0022_offer_meta'),
    ]

